#include <iostream>
#include <string>
#include <boost/regex.hpp>

std::string markdownToHtml(const std::string& markdownText) {
    std::string htmlText = "<html><head><title>Markdown to HTML</title></head><body>";
    std::string markdownRegex = "^#\\s*(.*)$";
    boost::regex markdownHeaderRegex(markdownRegex);
    boost::smatch match;
    while (boost::regex_search(markdownText, match, markdownHeaderRegex)) {
        htmlText += "<h" + std::to_string(match.size() + 1) + ">" + match.str(1) + "</h" + std::to_string(match.size() + 1) + ">";
        markdownText.erase(0, match.position() + match.length());
    }
    htmlText += "</body></html>";
    return htmlText;
}

